    private void btnRegisterActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnRegisterActionPerformed
        // TODO add your handling code here:
        String newUsername = txtUsername.getText();
        String newPassword = String.valueOf(txtPassword.getPassword());
        String nama = txtNama.getText();

        if (newUsername.isEmpty() || newPassword.isEmpty() || nama.isEmpty()) {
            JOptionPane.showMessageDialog(this, "New Username, Password, atau Nama tidak boleh kosong!");
        } else {
            if (cekUsername(newUsername)) {
                JOptionPane.showMessageDialog(this, "Username sudah ada. Silahkan coba yang lain!.");
            } else {
                if (registerUser(newUsername, newPassword, nama)) {
                    JOptionPane.showMessageDialog(this, "User Berhasil Didaftarkan!");
                    dispose();
                } else {
                    JOptionPane.showMessageDialog(this, "Failed to register user. Please try again.");
                }
            }
        }
    }//GEN-LAST:event_btnRegisterActionPerformed

