    private static boolean cekUsername(String username) {
        Connection connection = Koneksi.getKoneksi();
        Statement statement = null;
        ResultSet resultSet = null;

        try {
            statement = connection.createStatement();
            String query = "SELECT * FROM users WHERE username = '" + username + "'";
            resultSet = statement.executeQuery(query);

            return resultSet.next();
        }catch (SQLException e) {
            e.printStackTrace();
        }

        return false;
    }
    
    private boolean registerUser(String newUsername, String newPassword, String nama) {
        Connection connection = Koneksi.getKoneksi();
        PreparedStatement preparedStatement = null;

        try {
            String query = "INSERT INTO users (username, password, nama, tipe_akun) VALUES (?, ?, ?, 'pengguna')";
            preparedStatement = connection.prepareStatement(query);
            preparedStatement.setString(1, newUsername);
            preparedStatement.setString(2, newPassword);
            preparedStatement.setString(3, nama);

            int rowsAffected = preparedStatement.executeUpdate();
            return rowsAffected > 0;
        } catch (SQLException e) {
            JOptionPane.showMessageDialog(this, "Data Error " + e.getMessage());
        }
        return false;
    }
    
